declare type FileModification = {
    path: string;
    linesAdded?: number;
    linesDeleted?: number;
};
declare type FileRename = {
    oldPath: string;
    newPath: string;
};
declare type GitCommit = {
    hash: string;
    authorName: string;
    authorEmail: string;
    date: string;
    message: string;
    filesAdded: FileModification[];
    filesDeleted: FileModification[];
    filesModified: FileModification[];
    filesRenamed: FileRename[];
};
export type { GitCommit, FileModification, FileRename };
